'modules/Peg':function(){
define([
  "dojo/_base/declare"
  , "modules/Text"
  , "dojo/Deferred"
  , "dojo/has"
  , "sol/promise"
  , "dojo/_base/array"
  , "dojo/_base/lang"
  , "sol/string"
  , "main/clientOnly!./peg.js/Parser"
  , "main/clientOnly!dijit/form/Button"
  , "main/clientOnly!dojo/dom-class"
], function(
  declare
  , Base
  , Deferred
  , has
  , solPromise
  , array
  , lang
  , solString
  , Parser
  , Button
  , domClass
){
  
  var additionalSubtypes = {
    "peg.js": true
    , "pegjs": true
  };
    
  var additionalTypes = {
  };
  
  return declare([Base], {
    "class": "content text pegjs"
    
    , isCompetentPs: function(par){
      var def = this.def();
      
      var isPeg = false;
      
      var subtype = par.contentType.split("/")[1];
      if (additionalSubtypes[subtype]){
        isPeg = true;
      };
      
      if (!isPeg){
        if (additionalTypes[par.contentType]){
          isPeg = true;
        };
      };
      
      
      if (isPeg){
        def.resolve();
      }else{
        def.reject();
      };
      
      return def;
    }
    
    , buildRendering: function(){
      var ret = this.inherited(arguments);
      this.parseBtn = this.ownObj(new Button({
        onClick: lang.hitch(this, "openparser")
        , label: "try Parser"
      })); 
      this.menu.addChild(this.parseBtn);
      this.closeParseBtn = this.ownObj(new Button({
        onClick: lang.hitch(this, "closeparser")
        , label: "close Try Bar"
      })); 
      domClass.add(this.closeParseBtn.domNode, "invisible");
      this.menu.addChild(this.closeParseBtn);
      
      this.reinitParserBtn = this.ownObj(new Button({
        onClick: lang.hitch(this, "reinitParser")
        , label: "reapply Peg Code"
      })); 
      domClass.add(this.reinitParserBtn.domNode, "invisible");
      this.menu.addChild(this.reinitParserBtn);
      return ret;
    }
    
    , openparser: function(){
      domClass.add(this.parseBtn.domNode, "invisible");
      domClass.remove(this.closeParseBtn.domNode, "invisible");
      domClass.remove(this.reinitParserBtn.domNode, "invisible");
      //this.menu.resize();
      if (!this.parser){
        this.parser = this.ownObj(new Parser({
          parent: this
        }));
        this.mirror.on("change", lang.hitch(this, function(){
          this.parser.parserCodeChanged();
        }));
      };
      this.addChild(this.parser);
      this.parser.parse();
    }
    , closeparser: function(){
      domClass.remove(this.parseBtn.domNode, "invisible");
      domClass.add(this.closeParseBtn.domNode, "invisible");
      domClass.add(this.reinitParserBtn.domNode, "invisible");
      //this.menu.resize();
      this.removeChild(this.parser);
    }
    
    , reinitParser: function(){
      
    }
    
  });
});

},
'modules/peg.js/Parser':function(){
define([
  "dojo/_base/declare"
  , "dijit/layout/BorderContainer"
  , "sol/wgt/CodeMirror"
  , "sol/wgt/ObjectInspector"
  , "peg/Peg"
  , "dojo/_base/lang"
], function(
  declare
  , BorderContainer
  , CodeMirror
  , ObjectInspector
  , Peg
  , lang
){
  return declare([BorderContainer], {
    "class": "pegjs parser"
    , region: "right"
    , gutters: false
    , splitter: true
    , buildRendering: function(){
      this.inherited(arguments);
      this.mirror = this.ownObj(new CodeMirror({
        "class": "pegjs input"
        , region: "top"
        , splitter: true
      }));
      this.addChild(this.mirror);
      this.mirror.on("change", lang.hitch(this, function(){
        if (this._started){
          this.parse();
        };
      }));
      
      this.inspector = this.ownObj(new ObjectInspector({
        "class": "pegjs output"
        , region: "center"
        , value: undefined
        , expandlevel: 0
      }));
      this.inspector.region = "center";
      this.addChild(this.inspector);
    }
    , parse: function(){
      if (!this.parserCode){
        this.parserCode = this.parent.mirror.get("value");
        this.parser = Peg.buildParser(this.parserCode);
      };
      
      try{
        var parsed = this.parser.parse(this.mirror.get("value"));
        this.inspector.set("name", "result");
        this.inspector.set("value", parsed);
      }catch(e){
        this.inspector.set("name", "error");
        this.inspector.set("value", e);
      }
    }
    , parserCodeChanged: function(){
      this.parserCode = this.parent.mirror.get("value");
      this.parser = Peg.buildParser(this.parserCode);
    }
  });
});
},
